module.exports = function(RED){
	function createMNISTsetNode(config){
		const path = require('path')
		const utils = require('../../../utils/utils')

		var node = this;
		node.file = __dirname + '/mnist-dataset.py'

		node.config = {
			path: config.path
			
		}

		utils.run(RED, node, config)
	}
	RED.nodes.registerType("mnist dataset", createMNISTsetNode)
}
